lue="<?=$tma['tema'];?>"><?=$tma['tema'];?>. <?=$tma['nama_tema'];?></option>									<?php } ?>								</select>							</div>							<?php }; ?>							<div class="form-group form-group-default">								<label>Kompetensi Dasar</label>								<?php 								  $sqap=mysqli_query($koneksi, "select * from kd where kelas='$ab' and aspek='$peta' and mapel='$mpid'"); 								  $cekd=mysqli_num_rows($sqap);								  if($cekd>0){								  ?>									<select class="form-control" name="kd">									<?php while($apk=mysqli_fetch_array($sqap)){ ?>									<option value="<?=$apk['kd'];?>"><?=$apk['kd'];?></option>									<?php }; ?>									</select>								  <?php }else{ ?>									<p class="form-control-static">Belum ada Kompetensi Dasar</p>								  <?php }; ?>							</div>                        </div>                        <div class="modal-footer">                            <button type="button" class="btn btn-default waves-effect" data-dismiss="modal">Tutup</button>							<?php if($mpid==0){}else{ ?>                            <button type="submit" class="btn btn-danger waves-effect waves-light">Simpan</button>							<?php } ?>                        </div><?php}?>
