<?phpinclude("../../function/db.php");function TanggalIndo($tanggal){	$bulan = array ('Januari',				'Februari',				'Maret',				'April',				'Mei',				'Juni',				'Juli',				'Agustus',				'September',				'Oktober',				'November',				'Desember'			);	$split = explode('-', $tanggal);	return $split[2] . ' ' . $bulan[ (int)$split[1]-1 ] . ' ' . $split[0];};$kelas=$_POST['kelas'];$tapel=$_POST['tapel'];$idr=$_POST['rowid'];$tgl=$_POST['tgl'];$cek="SELECT * FROM siswa WHERE peserta_didik_id='$idr'";$hasil=mysqli_query($koneksi,$cek);$bio=mysqli_fetch_array($hasil);$ids=$bio['peserta_didik_id'];$day = date('D', strtotime($tgl));	$dayList = array(		'Sun' => 'Minggu',		'Mon' => 'Senin',		'Tue' => 'Selasa',		'Wed' => 'Rabu',		'Thu' => 'Kamis',		'Fri' => 'Jumat',		'Sat' => 'Sabtu'	);?>
						<?php if($dayList[$day]==="Sabtu" || $dayList[$day]==="Minggu"){ ?>						<div class="modal-body">							<div class="alert alert-info alert-dismissible">								<h4><i class="icon fa fa-info"></i> Error</h4>								Hari <?=$dayList[$day];?> Tidak bisa Absen ya :-)							</div>						</div>						<?php }else{ ?>						<div class="modal-body">							<div class="row">								<div class="col-md-12">									<div class="form-group form-group-default">										<label>Tanggal</label>										<input type="hidden" name="kelas" class="form-control" value="<?php echo $kelas;?>">										<input type="hidden" name="tanggals" class="form-control" value="<?=$tgl;?>">										<input type="hidden" name="pdid" class="form-control" value="<?=$idr;?>">										<input type="text" class="form-control" name="username" placeholder="Name" value="<?=TanggalIndo($tgl);?>">									</div>								</div>							</div>							<div class="row">								<div class="col-md-12">									<div class="form-group form-group-default">										<label>Nama Siswa</label>										<input type="text" name="nama" class="form-control" value="<?=$bio['nama'];?>">									</div>								</div>								<div class="col-md-12">									<div class="form-group">										<label class="form-label d-block">Absensi</label>										<div class="selectgroup selectgroup-secondary selectgroup-pills">											<label class="selectgroup-item">												<input type="radio" name="jnabsen" value="S" class="selectgroup-input">												<span class="selectgroup-button selectgroup-button-icon">S</span>											</label>											<label class="selectgroup-item">												<input type="radio" name="jnabsen" value="I" class="selectgroup-input">												<span class="selectgroup-button selectgroup-button-icon">I</span>											</label>											<label class="selectgroup-item">												<input type="radio" name="jnabsen" value="A" class="selectgroup-input">												<span class=